def howmany(journalists)
	puts "Combien y a-t-il de journalistes dans ce array ?"
	return journalists.count
end

howmany(journalists)
puts journalists.count

def howmanynb(journalists)
	puts "Combien d'handle contiennent un numéro?"
	return journalists.grep(/[0-9]/).count
	
end

howmanynb(journalists)
puts journalists.grep(/[0-9]/).count

def howmanyaude(journalists)
	puts "Combien d'handle contiennent les 4 lettres du prénom Aude à la suite (sans prendre en compte les majuscule) ?"
	return journalists.grep(/aude/i).count
end 

howmanyaude(journalists)
puts journalists.grep(/aude/i).count

def hmmaj(journalists)
	puts "Combien commencent par une majuscule (première lettre juste après le @) ?"
	return journalists.count {|e| e =~ /^@[A-Z]/}
end

hmmaj(journalists)
puts journalists.count {|e| e =~ /^@[A-Z]/}

def hmmaj2(journalists)
	puts "Combien contiennent au moins une majuscule ?"
	return journalists.grep(/[A-Z]/).count
end

hmmaj2(journalists)
puts journalists.grep(/[A-Z]/).count

def under(journalists)
	puts "Combien y a-t-il de underscore dans tous les pseudos confondus ?"
	return journalists.grep(/[_]/).count
end 

under(journalists)
puts journalists.grep(/[_]/).count

def AZ(journalists)
	puts "Trie la liste de handle par ordre alphabétique"
	return journalists.sort_by {|word| word.downcase}
end 

AZ(journalists)
puts journalists.sort_by {|word| word.downcase}

